
public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 3;
		
		double c = (double)a/b; // TypeCasting 
		int result = a%b; // finding Remainder
		
		
		
		System.out.println(c); // Decimal Value by type casting
		System.out.println(result); // Reminder
			
		
	}

}
